script:
cur_frm.add_fetch("stranka", "customer_name", "item_name");
frappe.ui.form.on("Item", "stranka", function (frm) {
	if (frm.doc.item_code == null) {
		//msgprint("test")
		cur_frm.cscript.custom_validate = function (doc) {
			// clear item_code (name is from item_code)
			doc.item_code = "";
			var date = "-" + moment(doc.datum).format('DDMMYY');
			// first 2 characters based on item_group
			switch (doc.item_group) {
			case "Punčka":
				doc.item_code = "MPNV-" + doc.item_name + date;
				break;
			case "Punčka mala":
				doc.item_code = "MPNM-" + doc.item_name + date;
				break;
			case "Medo":
				doc.item_code = "MEDO-" + doc.item_name + date;
				break;
			case "Broška":
				doc.item_code = "BR-" + doc.item_name + date;
				break;
			case "Žival":
				doc.item_code = "ZIV-" + doc.item_name + date;
				break;
case "Zajček":
				doc.item_code = "ZAJ-" + doc.item_name + date;
				break;
			case "VIP":
				doc.item_code = "VIP-" + doc.item_name + date;
				break;
			default:
				doc.item_code = "MIMA-" + doc.item_name + date;
			}
			//msgprint(doc.item_code);
		};
		var str = doc.item_code;
		doc.item_name = str.substring(0, 5) + doc.item_name;
	};
});

frappe.ui.form.on("Sales Invoice", "after_save", function(frm) {
            //msgprint(frm.doc.name);
if(frm.doc.racun == undefined){
str = frm.doc.name; 
cur_frm.doc.racun = str.substring(3);}      

});
